import numpy as np

def select_next_node(current_node: int, depot: int, unvisited_nodes: np.ndarray, rest_capacity: np.ndarray, demands: np.ndarray, distance_matrix: np.ndarray) -> int:
    if len(unvisited_nodes) == 0:
        return depot

    cap = float(rest_capacity)
    feasible = np.array([n for n in unvisited_nodes if demands[n] <= cap], dtype=int)
    if len(feasible) == 0:
        return depot

    # Cost proxy for committing to n now: cur->n plus n->depot pull
    cost = distance_matrix[current_node, feasible] + 0.4 * distance_matrix[feasible, depot]

    if len(feasible) == 1:
        return int(feasible[0])

    k = min(3, len(feasible))
    order = np.argsort(cost)

    # Regret defined as kth_best - best
    regret = float(cost[order[k - 1]] - cost[order[0]])

    # Turn scalar regret into selection: choose the node that is best (order[0]) if regret is small,
    # otherwise choose the node at order[1] to avoid over-committing (a distinct behavior).
    if regret < (np.std(cost) + 1e-9):
        return int(feasible[int(order[0])])
    return int(feasible[int(order[min(1, len(order) - 1)])])

