    # Non-dominated wrt minimize d_cur and maximize dem
    nd_mask = np.ones(len(feasible), dtype=bool)
    for i in range(len(feasible)):
        if not nd_mask[i]:
            continue
        for j in range(len(feasible)):
            if i == j:
                continue
            if (d_cur[j] <= d_cur[i] + 1e-12) and (dem[j] >= dem[i] - 1e-12) and ((d_cur[j] < d_cur[i] - 1e-12) or (dem[j] > dem[i] + 1e-12)):
