    if len(feasible) == 1:
        return int(feasible[0])

    ord_idx = np.argsort(cost)
    best = float(cost[ord_idx[0]])
    kth = float(cost[ord_idx[min(2, len(ord_idx)-1)]])
    regret = kth - best

    # map scalar regret to choice: if regret large, take the top candidate; else take the median candidate (diversify)
